<template>
  <div class="home">
    <div id="map" style="width: 800px; height: 450px"></div>
  </div>
</template>

<style>
#marker {
  background-image: url("data:image/jpeg;base64,");
  background-size: cover;
  width: 50px;
  height: 50px;
  border-radius: 50%;
  cursor: pointer;
}

.mapboxgl-popup {
  max-width: 200px;
}
</style>

<script>
import "mapbox-gl/dist/mapbox-gl.css";
import mapboxgl from "mapbox-gl"; // or "const mapboxgl = require('mapbox-gl');"

export default {
  mounted: function () {
    this.doMapBox();
  },
  data: function () {
    return {
      places: [
        {
          lat: 55.6929,
          lng: 12.5993,
          description: "A mermaid in Copenhagen",
        },
        {
          lat: 55.6749,
          lng: 12.5689,
          description: "A big house a famous guy owned",
        },
      ],
    };
  },
  methods: {
    doMapBox: function () {
      var center = [12.5993, 55.6929];
      mapboxgl.accessToken = process.env.VUE_APP_MAP_BOX_API_KEY;
      const map = new mapboxgl.Map({
        container: "map", // container ID
        style: "mapbox://styles/mapbox/dark-v10", // style URL
        center: center, // starting position [lng, lat]
        zoom: 9, // starting zoom
      });
      this.places.forEach((place) => {
        console.log(place);
        var marker1 = new mapboxgl.Marker()
          .setLngLat([place.lng, place.lat])
          .addTo(map)
          .setPopup(
            new mapboxgl.Popup({ offset: 25 }).setText(place.description)
          );
      });
    },
  },
};
</script>
